@import url('https://fonts.googleapis.com/css2?family=Bebas+Neue&family=Noto+Serif:wght@700&family=Roboto+Serif&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Barriecito&family=Bebas+Neue&family=Noto+Serif:wght@700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Lobster&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Architects+Daughter&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Alegreya:ital,wght@1,500&display=swap');
$background: #d4d0ac;
$paragraph: #2a2a2a;
$button: #ff8e3c;
$button-text: #0d0d0d;
$headline: #292828;


//Sizes
$h1: 20px;
$h1-tablet-desktop: 30px;   

//letter-sizes
$tablet: 768px;
$desktop: 992px;

//backgraund article
$back-art-color: $background;

//size of nav
$size-nav: 12px;
$family-nav: 'Bebas Neue', cursive; 

//margin nav
$margin-nav: 5px;

//font.family 
$font-h1: 'Lobster', cursive;

//parrafos
$size-p:12px;
$family-p: 'Architects Daughter', cursive;
$align-p: justify;
$size-p-foot: 10px;

//fomt-family H1,H2
$family-h2:'Alegreya', serif; 
$Sizefamily-h2: 14px;
$Sizefamily-h1: 20px;

